import React from 'react';
import './App.css';
import { Button } from 'react-bootstrap';


function Tile(props) {
  let res = " "
  if(props.votes > 0)
    res = props.votes;
  return(
    <div className="col-lg-4 col-md-4 col-6">
      <div key={props.key} className="tile" onClick={props.onClick}>
      </div>
    </div>
  );
}

class Votes extends React.Component {
  constructor(props) {
    // let colours =
    super(props);
    this.state = {
      oneFlipped: false,
      tileVotes: this.randomiseColours(),
    }
  }

  handleClick(i) {
    let totalPresses = this.state.tileVotes;
    totalPresses[i]++;
    this.setState({
      // oneFlipped: !oneFlipped,
      tileVotes: totalPresses,
    });
  }

  randomiseColours() {
    let allColours = ['#b99cff','#ed9cff','#ff9ca9','#ffca9c','#f8ff9c',
    '#9cffb0','#9cfff3','#9cc4ff', '#9c9cff'];
    let res = [];

    // first populate array
    let i, c;
    i = c = 0;
    while(i < allColours.length*2) {
      if(i % 2 != 0)
        c++;
      res.push(allColours[c]);
      i++
    }
    console.log(res);

    // then randomise it
    for(let i = 0; i < res.length; i++) {
      // get random index and swap them
      let randI = Math.floor(Math.random()*res.length);
      let _tmp = res[randI];
      res[randI] = res[i];
      res[i] = _tmp;
    }
    console.log(res);
    return res;
  }

  renderTiles() {
    let res = [];

    for(let i = 0; i < 18; ++i) {
      res.push(<Tile key={i.toString()} onClick={() => this.handleClick(i)}/>);
    }

    return (res);
  }

  render() {
    return(
      <div className="container">
        <h1> {this.props.title} </h1>
        <div className="row">
        {
          this.renderTiles()
        }
        </div>
      </div>

    );
  }

}


function App() {
  return (
    <div className="App">
      <header className="App-header">
        <Votes
        title={"Match the Colours!"}
        />
      </header>
    </div>
  );
}

export default App;
