import React from 'react';
import './App.css';
import { Button } from 'react-bootstrap';


function VoteCard(props) {
  let res = ""
  if(props.votes > 0)
    res = props.votes;
  return(
    <div className={props.divAlign}>
      <h2>{props.text}</h2>
      <h3>{res}</h3>
      <Button variant="secondary" className="mr-2" onClick={props.onClick}>vote</Button>
    </div>
  );
}

class Votes extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      tileVotes: Array(4).fill(0),
    }
  }

  handleClick(i) {
    let totalPresses = this.state.tileVotes;
    totalPresses[i]++;
    this.setState({
      tileVotes: totalPresses,
    })

    console.log(this.state.tileVotes);
  }

  render() {
    return(
      <div className="container">
        <h1> {this.props.title} </h1>
        <div className="row">
          <VoteCard
            divAlign={"col-lg-3 col-sm-6 tile"}
            text={"North"}
            votes={this.state.tileVotes}
            onClick={() => this.handleClick(0)}
          />
          <VoteCard
            divAlign={"col-lg-3 col-sm-6 tile"}
            text={"East"}
            votes={this.state.tileVotes}
            onClick={() => this.handleClick(1)}
          />
          <VoteCard
            divAlign={"col-lg-3 col-sm-6 tile"}
            text={"South"}
            votes={this.state.tileVotes}
            onClick={() => this.handleClick(2)}
          />
          <VoteCard
            divAlign={"col-lg-3 col-sm-6 tile"}
            text={"West"}
            votes={this.state.tileVotes}
            onClick={() => this.handleClick(3)}
          />
        </div>
      </div>

    );
  }

}


function App() {
  return (
    <div className="App">
      <header className="App-header">
        <Votes
        title={"Vote on the best direction!"}
        />
      </header>
    </div>
  );
}

export default App;
