import React from 'react';
import './App.css';
import { Button } from 'react-bootstrap';


function Tile(props) {
  let res = " "
  if(props.votes > 0)
    res = props.votes;
  return(
    <div className={props.divAlign}>
      <h2>{props.text}</h2>
      <h3>{res}</h3>
      <Button variant="secondary" className="mr-2" onClick={props.onClick}>vote</Button>
    </div>
  );
}

class Votes extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      tileVotes: Array(4).fill(0),
    }
  }

  handleClick(i) {
    let totalPresses = this.state.tileVotes;
    totalPresses[i]++;
    this.setState({
      tileVotes: totalPresses,
    });
    // var fs = require("fs");
    // console.log(fs);
    // fs.readFile("data.txt", (err, data) => {
    //   if(err) throw err;
    //   console.log(data.toString());
    // })
  }

  render() {
    return(
      <div className="container">
        <h1> {this.props.title} </h1>
        <div className="row">
          <Tile
            divAlign={"col-4 tile"}
            text={"North"}
            votes={this.state.tileVotes[0]}
            onClick={() => this.handleClick(0)}
          />
          <Tile
            divAlign={"col-4 tile"}
            text={"East"}
            votes={this.state.tileVotes[1]}
            onClick={() => this.handleClick(1)}
          />
          <Tile
            divAlign={"col-4 tile"}
            text={"South"}
            votes={this.state.tileVotes[2]}
            onClick={() => this.handleClick(2)}
          />
          <Tile
            divAlign={"col-4 tile"}
            text={"West"}
            votes={this.state.tileVotes[3]}
            onClick={() => this.handleClick(3)}
          />
          <Tile
            divAlign={"col-4 tile"}
            text={"North"}
            votes={this.state.tileVotes[0]}
            onClick={() => this.handleClick(0)}
          />
          <Tile
            divAlign={"col-4 tile"}
            text={"North"}
            votes={this.state.tileVotes[0]}
            onClick={() => this.handleClick(0)}
          />
          <Tile
            divAlign={"col-4 tile"}
            text={"North"}
            votes={this.state.tileVotes[0]}
            onClick={() => this.handleClick(0)}
          />
          <Tile
            divAlign={"col-4 tile"}
            text={"North"}
            votes={this.state.tileVotes[0]}
            onClick={() => this.handleClick(0)}
          />
          <Tile
            divAlign={"col-4 tile"}
            text={"North"}
            votes={this.state.tileVotes[0]}
            onClick={() => this.handleClick(0)}
          />
        </div>
      </div>

    );
  }

}


function App() {
  return (
    <div className="App">
      <header className="App-header">
        <Votes
        title={"Vote on the best direction!"}
        />
      </header>
    </div>
  );
}

export default App;
