import React from 'react';
import './App.css';
import { Button } from 'react-bootstrap';


function Tile(props) {
  let res = " "
  if(props.votes > 0)
    res = props.votes;
  return(
    <div className="col-lg-4 col-md-4 col-6">
      <div key={props.key} className="tile" onClick={props.onClick}>
      </div>
    </div>
  );
}

class Votes extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      oneFlipped: false,
      colours: this.randomiseColours(),
    }
  }

  handleClick(i) {
    let totalPresses = this.state.tileVotes;
    totalPresses[i]++;
    this.setState({
      // oneFlipped: !oneFlipped,
      tileVotes: totalPresses,
    });
  }

  randomiseColours() {
    let allColours = ['#b99cff','#ed9cff','#ff9ca9','#ffca9c','#f8ff9c',
    '#9cffb0','#9cfff3','#9cc4ff', '#9c9cff'];
    let doubles = [];
    // const res = {[]}

    // first populate array
    let i = 0;
    while(i < allColours.length) {
      doubles.push(allColours[i]);
      doubles.push(allColours[i]);
      i++;
    }
    // console.log("res before is is: ", res);

    // then randomise it
    for(let i = 0; i < doubles.length; i++) {
      // get random index and swap them
      let randI = Math.floor(Math.random()*doubles.length);
      let _tmp = doubles[randI];
      doubles[randI] = doubles[i];
      doubles[i] = _tmp;
    }

    // for(let i = 0; i < doubles.length; i++) {
    //   res
    // }

    // console.log("Res is: ", res);
    return doubles;
  }

  renderTiles() {
    let res = [];

    const colours = this.state.colours;
    let i = 0;
    const listItems = colours.map((colour) =>
      res.push(<Tile key={i.toString()} onClick={() => this.handleClick(i)}/>),
      i++,
    );

    // for(let i = 0; i < 18; ++i) {
    //   res.push(<Tile key={i.toString()} onClick={() => this.handleClick(i)}/>);
    // }

    return (res);
  }

  render() {
    return(
      <div className="container">
        <h1> {this.props.title} </h1>
        <div className="row">
        {
          this.renderTiles()
        }
        </div>
      </div>

    );
  }

}


function App() {
  return (
    <div className="App">
      <header className="App-header">
        <Votes
        title={"Match the Colours!"}
        />
      </header>
    </div>
  );
}

export default App;
