import React from 'react';
import './App.css';
import { Button } from 'react-bootstrap';


function Tile(props) {
  let res = " "
  if(props.votes > 0)
    res = props.votes;
  return(
    <div className="col-lg-4 col-md-4 col-12">
      <div className="tile" onClick={props.onClick}>
      </div>
    </div>
  );
}

class Votes extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      oneFlipped: false,
      tileVotes: Array(9).fill(0),
    }
  }

  handleClick(i) {
    let totalPresses = this.state.tileVotes;
    totalPresses[i]++;
    this.setState({
      // oneFlipped: !oneFlipped,
      tileVotes: totalPresses,
    });
  }

  // randomiseColours() {
  //   let arr = Array(9);
  //   for(let i = 0; i < 9; i++) {
  //     Math.floor(Math.random()*9);
  //     arr[]
  //   }
  // }

  renderTiles() {
    let res = [];

    for(let i = 0; i < 18; ++i) {
      res.push(<Tile onClick={() => this.handleClick(i)}/>);
    }

    return (res);
  }

  render() {
    return(
      <div className="container">
        <h1> {this.props.title} </h1>
        <div className="row">
        {
          this.renderTiles()
        }
        </div>
      </div>

    );
  }

}


function App() {
  return (
    <div className="App">
      <header className="App-header">
        <Votes
        title={"Match the Colours!"}
        />
      </header>
    </div>
  );
}

export default App;
