import React from 'react';
import logo from './logo.svg';
import './App.css';
import { Button } from 'react-bootstrap';


function VoteCard(props) {
  return(
    <div>
      <h2>{props.text}</h2>
      <Button variant="secondary" className="mr-2" onClick={props.onClick}>vote</Button>
    </div>
  );
}

class Votes extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      numberOfSelections: 0,
      buttonPresses: [],
    }
  }

  handleClick() {
    let totalPresses = this.state.buttonPresses;
    totalPresses++;
    this.setState({
      buttonPresses: totalPresses,
    })
    console.log(this.state.buttonPresses);
  }

  render() {
    return(
      <div>
        <h1> {this.props.param1} </h1>
        <div>
          <VoteCard
            text={"North"}
            onClick={() => this.handleClick()}
          />
          <VoteCard
            text={"East"}
            onClick={() => this.handleClick()}
          />
          <VoteCard
            text={"South"}
            onClick={() => this.handleClick()}
          />
          <VoteCard
            text={"West"}
            onClick={() => this.handleClick()}
          />
        </div>
      </div>

    );
  }

}


function App() {
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <Votes
        title={"Vote on the best section of SFU!"}
        />
      </header>
    </div>
  );
}

export default App;
