import React from 'react';
import './App.css';


class Tile extends React.Component {
  constructor(props) {
    super(props);
    this.myRef = React.createRef();
    console.log(this.myRef);
  }
  render() {
    return(
      <div className="col-lg-2 col-md-2 col-sm-2 col-4">
        <div className="tile" ref={this.myRef} onClick={this.props.onClick} style={{backgroundColor: this.props.colour}}>
        </div>
      </div>
    );
  }
}


// function Tile(props) {
//   return(
//     <div className="col-lg-2 col-md-2 col-sm-2 col-4">
//       <div className="tile" onClick={props.onClick} style={{backgroundColor: props.colour}}>
//       </div>
//     </div>
//   );
// }

class Votes extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      roundScore: 0,
      numFlipped: 0,
      colourToMatch: null,
      indexOfLast: null,
      colours: this.randomiseColours(),
    }
  }

  handleClick(colour, index) {
    this.state.numFlipped++;
    if(this.state.numFlipped === 2) {
      if(this.state.colourToMatch === colour && this.state.indexOfLast !== index) {
        console.log("Nice, that's a match");
        let newScore = this.state.roundScore++;
        this.setState({
          roundScore: newScore,
        });  let res = [];
      } else {
        console.log("Not epic, not a match");
      }
      this.setState({
        numFlipped: 0,
        colourToMatch: null,
        indexOfLast: null,
      });
    }
    else if(this.state.numFlipped === 1) {
      console.log("waiting for next...")
      this.setState({
        colourToMatch: colour,
        indexOfLast: index,
      });
    }

  }

  randomiseColours() {
    let allColours = ['#b99cff','#ed9cff','#ff9ca9','#ffca9c','#f8ff9c',
    '#9cffb0','#9cfff3','#9cc4ff', '#9c9cff'];
    let doubles = [];

    // first populate array
    let i = 0;
    while(i < allColours.length) {
      doubles.push(allColours[i]);
      doubles.push(allColours[i]);
      i++;
    }

    // then randomise it
    for(let i = 0; i < doubles.length; i++) {
      // get random index and swap them
      let randI = Math.floor(Math.random()*doubles.length);
      let _tmp = doubles[randI];
      doubles[randI] = doubles[i];
      doubles[i] = _tmp;
    }

    return doubles;
  }

  renderTiles() {
    let res = [];

    const colours = this.state.colours;

    const listItems = colours.map((colour, index) =>
      res.push(<Tile
        key={index.toString()}
        onClick={() => this.handleClick(colour, index)}
        colour={colour}
        numFlipped={this.state.numFlipped}
        />),
    );

    return (res);
  }

  removeColor() {

  }

  render() {
    return(
      <div className="container">
        <h1> {this.props.title} </h1>
        <div className="row">
        {
          this.renderTiles()
        }
        </div>
      </div>

    );
  }

}


function App() {
  return (
    <div className="App">
      <header className="App-header">
        <Votes
        title={"Match the Colours!"}
        />
      </header>
    </div>
  );
}

export default App;
