import React from 'react';
import logo from './logo.svg';
import './App.css';


function VoteCard(props) {
  return(
    <div>
      <h2>{props.text}</h2>
      <button onClick={props.onClick}>vote</button>
    </div>
  );
}

class Votes extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      numberOfSelections: 0,
      buttonPresses: [],
    }
  }

  handleClick() {
    let totalPresses = this.state.buttonPresses;
    totalPresses++;
    this.setState({
      buttonPresses: totalPresses,
    })
    console.log(this.state.buttonPresses);
  }

  render() {
    return(
      <div>
        <p>
        {this.props.param1}
        </p>
        <p>
        {this.props.param2}
        </p>
        <VoteCard
          text={"textname"}
          onClick={() => this.handleClick()}
        />
      </div>

    );
  }

}


function App() {
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <Votes
        param1={"pants"}
        param2={"Yoop"}
        />
      </header>
    </div>
  );
}

export default App;
